import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import './Videos.css';

function Videos() {
  const { tmdbId } = useParams();
  const [videos, setVideos] = useState([]);
  const [formState, setFormState] = useState({ mode: 'base', video: {} });
  const [data, setData] = useState({
    url: '',
    name: '',
    site: '',
    videoKey: '',
    videoType: '',
    official: 0,
  });

  const [searchQuery, setSearchQuery] = useState('');
  const [importedVideos, setImportedVideos] = useState([]);
  const [popupMessage, setPopupMessage] = useState('');
  const [showPopup, setShowPopup] = useState(false);
  const [loading, setLoading] = useState(false);  // Track loading state

  const accessToken = localStorage.getItem('accessToken');
  const user = JSON.parse(localStorage.getItem('user'));

  useEffect(() => {
    axios.get('/videos', {
      headers: { Authorization: `Bearer ${accessToken}` }
    }).then(response => {
      setVideos(response.data);
    }).catch(error => {
      console.error('Error fetching videos:', error);
    });
  }, [accessToken]);

  // Handle the change in form fields
  const handleOnChange = (e) => {
    const { name, value } = e.target;
    if (formState.mode === 'add') {
      setData((prev) => ({ ...prev, [name]: value }));
    } else {
      setFormState((prev) => ({
        ...prev,
        video: { ...prev.video, [name]: value }
      }));
    }
  };

  // Show popup message
  const showPopUpMessage = (message) => {
    setPopupMessage(message);
    setShowPopup(true);
    setTimeout(() => {
      setShowPopup(false);
    }, 3000);
  };

  // Save video to database
  const handleSave = () => {
    setLoading(true);  // Show loading state
    const formData = new FormData();
    formData.append('userId', user.userId);
    formData.append('movieId', tmdbId);
    Object.keys(data).forEach((key) => formData.append(key, data[key]));

    axios.post('/videos', formData, {
      headers: {
        'Content-Type': 'multipart/form-data',
        Authorization: `Bearer ${accessToken}`
      }
    }).then(response => {
      setVideos([...videos, response.data]);
      setFormState({ mode: 'base', video: {} });
      showPopUpMessage('Video saved successfully!');
      setLoading(false);  // Hide loading state
    }).catch(error => {
      console.error('Error saving video:', error);
      showPopUpMessage('Error saving video!');
      setLoading(false);  // Hide loading state
    });
  };

  // Update video
  const handleUpdate = () => {
    setLoading(true);  // Show loading state
    const updatedVideo = formState.video;
    axios.patch(`/videos/${updatedVideo.id}`, updatedVideo, {
      headers: {
        'Content-Type': 'application/json',
        Authorization: `Bearer ${accessToken}`
      }
    }).then(response => {
      setVideos((prev) => prev.map(video => video.id === updatedVideo.id ? response.data : video));
      setFormState({ mode: 'base', video: {} });
      showPopUpMessage('Video updated successfully!');
      setLoading(false);  // Hide loading state
    }).catch(error => {
      console.error('Error updating video:', error);
      showPopUpMessage('Error updating video!');
      setLoading(false);  // Hide loading state
    });
  };

  // Save imported video to the database
  const handleSaveImportedVideos = (video) => {
    setLoading(true);  // Show loading state
    const formData = new FormData();
    formData.append('userId', user.userId);
    formData.append('movieId', tmdbId);
    formData.append('url', video.site);
    formData.append('name', video.name);
    formData.append('videoKey', video.key);
    formData.append('videoType', video.type);
    formData.append('official', video.official ? 1 : 0);

    axios.post('/videos', formData, {
      headers: {
        'Content-Type': 'multipart/form-data',
        Authorization: `Bearer ${accessToken}`
      }
    }).then(response => {
      setVideos([...videos, response.data]);
      showPopUpMessage('Imported video added successfully!');
      setLoading(false);  // Hide loading state
    }).catch(error => {
      console.error('Error saving imported video:', error);
      showPopUpMessage('Error adding imported video!');
      setLoading(false);  // Hide loading state
    });
  };

  // Delete a video
  const handleDelete = (videoId) => {
    setLoading(true);  // Show loading state
    axios.delete(`/videos/${videoId}`, {
      headers: {
        Authorization: `Bearer ${accessToken}`
      }
    }).then(response => {
      setVideos((prevVideos) => prevVideos.filter((video) => video.id !== videoId));
      showPopUpMessage('Video deleted successfully!');
      setLoading(false);  // Hide loading state
    }).catch(error => {
      console.error('Error deleting video:', error);
      showPopUpMessage('Error deleting video!');
      setLoading(false);  // Hide loading state
    });
  };

  // Search videos from TMDb
  const handleSearch = () => {
    if (!searchQuery) return; // Skip if search query is empty
    const apiKey = '67e39655ab5c0b2be23473d483fb4af4'; // TMDb API Key
    const url = `https://api.themoviedb.org/3/search/movie?query=${searchQuery}&api_key=${apiKey}`;

    axios.get(url).then((response) => {
      const movieId = response.data.results[0]?.id;
      if (movieId) {
        axios.get(`https://api.themoviedb.org/3/movie/${movieId}/videos?api_key=${apiKey}`).then((videoResponse) => {
          setImportedVideos(videoResponse.data.results);
        }).catch((error) => {
          console.error('Error fetching imported videos:', error);
        });
      }
    }).catch((error) => {
      console.error('Error searching for movie:', error);
    });
  };

  return (
    <div>
      {showPopup && <div className="popup">{popupMessage}</div>} {/* Display Pop-up Message */}

      <button onClick={() => setFormState({ mode: formState.mode === 'base' ? 'add' : 'base', video: {} })}>
        {formState.mode === 'base' ? 'Add Video' : 'Cancel'}
      </button>

      {/* Import Videos Button */}
      <button onClick={() => setFormState({ mode: 'base' })}>
        Import Videos from TMDb
      </button>

      <input
        type="text"
        placeholder="Search for a movie"
        value={searchQuery}
        onChange={(e) => setSearchQuery(e.target.value)}
      />
      <button onClick={handleSearch}>Search</button>

      {/* Display imported videos */}
      <div>
        {importedVideos.length > 0 && (
          <div>
            <h2>Imported Videos</h2>
            {importedVideos.map((video) => (
              <div key={video.id}>
                <h3>{video.name}</h3>
                {video.site === 'YouTube' && (
                  <iframe
                    width="560"
                    height="315"
                    src={`https://www.youtube.com/embed/${video.key}`}
                    title={video.name}
                    frameBorder="0"
                    allowFullScreen
                    className="video-iframe"
                  ></iframe>
                )}
                <button onClick={() => handleSaveImportedVideos(video)}>
                  Add to Movie
                </button>
              </div>
            ))}
          </div>
        )}
      </div>

      {(formState.mode === 'add' || formState.mode === 'update') && (
        <div>
          <form>
            {/* Form fields for video data */}
          </form>
          <button onClick={formState.mode === 'add' ? handleSave : handleUpdate} disabled={loading}>
            {loading ? 'Saving...' : 'Save Video'}
          </button>
        </div>
      )}

      {/* Display Videos */}
      <div>
        <h2>Videos</h2>
        {videos.map((video) => (
          <div key={video.id}>
            <h3>{video.name}</h3>
            <button onClick={() => handleDelete(video.id)}>
              Delete
            </button>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Videos;
