import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import './Videos.css'; // Ensure you have a corresponding CSS file for styling

function Videos() {
  const { tmdbId } = useParams();
  const navigate = useNavigate();
  const [videoInfo, setVideoInfo] = useState([]);
  const [formState, setFormState] = useState({ mode: "base", video: {} });
  const [data, setData] = useState({
    url: '',
    title: '',
    description: '',
  });
  const [searchQuery, setSearchQuery] = useState('');
  const [importedVideos, setImportedVideos] = useState([]);
  
  const accessToken = localStorage.getItem("accessToken");
  const user = JSON.parse(localStorage.getItem("user"));

  useEffect(() => {
    if (!accessToken) {
      // Redirect to login if the user is not logged in
      navigate('/login');
      return;
    }

    axios
      .get('/videos', {
        headers: { Authorization: `Bearer ${accessToken}` },
      })
      .then((response) => {
        setVideoInfo(response.data);
      })
      .catch((error) => console.error("Error fetching videos:", error));
  }, [accessToken, navigate]);

  const handleOnChange = (e) => {
    const { name, value } = e.target;
    if (formState.mode === "add") {
      setData((prev) => ({ ...prev, [name]: value }));
    } else {
      setFormState((prev) => ({
        ...prev,
        video: { ...prev.video, [name]: value },
      }));
    }
  };

  const handleSave = () => {
    if (!user) {
      alert("You need to be logged in to save a video.");
      return;
    }

    const formData = new FormData();
    formData.append('userId', user.userId);
    formData.append('movieId', tmdbId);
    Object.keys(data).forEach((key) => formData.append(key, data[key]));

    axios
      .post('/videos', formData, {
        headers: { 'Content-Type': 'multipart/form-data', Authorization: `Bearer ${accessToken}` },
      })
      .then((response) => {
        setVideoInfo([...videoInfo, response.data]);
        setFormState({ mode: "base", video: {} });
      })
      .catch((error) => console.error("Error saving video:", error));
  };

  return (
    <div>
      <button onClick={() => setFormState({ mode: formState.mode === "base" ? "add" : "base", video: {} })}>
        {formState.mode === "base" ? "Add Video" : "Cancel"}
      </button>
      <input
        type="text"
        placeholder="Search for a movie"
        value={searchQuery}
        onChange={(e) => setSearchQuery(e.target.value)}
      />
      {(formState.mode === "add" || formState.mode === "update") && (
        <div>
          <form>
            <label>Video URL<input type="text" name="url" value={data.url} onChange={handleOnChange} /></label>
            <label>Title<input type="text" name="title" value={data.title} onChange={handleOnChange} /></label>
            <label>Description<textarea name="description" value={data.description} onChange={handleOnChange} /></label>
          </form>
          <button onClick={handleSave}>Save</button>
        </div>
      )}
      <div>
        {videoInfo.map((video) => (
          <div key={video.id}>
            <h1>{video.title}</h1>
            <p>{video.description}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Videos;
