import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import './Videos.css';

function Videos() {
  const { tmdbId } = useParams();
  const [videoInfo, setVideoInfo] = useState([]);
  const [formState, setFormState] = useState({ mode: "base", video: {} });
  const [data, setData] = useState({
    url: '',
    title: '',
    description: '',
  });
  const [searchQuery, setSearchQuery] = useState('');
  const [importedVideos, setImportedVideos] = useState([]);
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  const [accessToken, setAccessToken] = useState(localStorage.getItem("accessToken"));
  const [user, setUser] = useState(JSON.parse(localStorage.getItem("user")));

  useEffect(() => {
    // Check if the user is logged in
    if (accessToken && user) {
      setIsLoggedIn(true);
    } else {
      setIsLoggedIn(false);
    }
  }, [accessToken, user]);

  // Fetch videos only if the user is logged in
  useEffect(() => {
    if (isLoggedIn) {
      axios
        .get('/videos', {
          headers: { Authorization: `Bearer ${accessToken}` },
        })
        .then((response) => {
          setVideoInfo(response.data);
        })
        .catch((error) => console.error("Error fetching videos:", error));
    }
  }, [accessToken, isLoggedIn]);

  // If user is not logged in, show the login message
  if (!isLoggedIn) {
    return <div>User is not logged in. Please log in first.</div>;
  }

  const handleOnChange = (e) => {
    const { name, value } = e.target;
    if (formState.mode === "add") {
      setData((prev) => ({ ...prev, [name]: value }));
    } else {
      setFormState((prev) => ({
        ...prev,
        video: { ...prev.video, [name]: value },
      }));
    }
  };

  const handleSave = () => {
    const formData = new FormData();
    formData.append('userId', user.userId); // Use userId from the logged-in user
    formData.append('movieId', tmdbId);
    Object.keys(data).forEach((key) => formData.append(key, data[key]));

    axios
      .post('/videos', formData, {
        headers: { 'Content-Type': 'multipart/form-data', Authorization: `Bearer ${accessToken}` },
      })
      .then((response) => {
        setVideoInfo([...videoInfo, response.data]);
        setFormState({ mode: "base", video: {} });
      })
      .catch((error) => console.error("Error saving video:", error));
  };

  const handleUpdate = () => {
    const updatedVideo = formState.video;
    axios
      .patch(`/videos/${updatedVideo.id}`, updatedVideo, {
        headers: { 'Content-Type': 'application/json', Authorization: `Bearer ${accessToken}` },
      })
      .then((response) => {
        setVideoInfo((prev) =>
          prev.map((video) => (video.id === updatedVideo.id ? response.data : video))
        );
        setFormState({ mode: "base", video: {} });
      })
      .catch((error) => console.error("Error updating video:", error));
  };

  const handleDelete = (id) => {
    if (window.confirm('Are you sure you want to delete this video?')) {
      axios
        .delete(`/videos/${id}`, {
          headers: { Authorization: `Bearer ${accessToken}` },
        })
        .then(() => {
          setVideoInfo(videoInfo.filter((video) => video.id !== id));
        })
        .catch((error) => console.error("Error deleting video:", error));
    }
  };

  const handleImportVideos = async () => {
    if (!searchQuery) {
      alert("Please enter a movie name.");
      return;
    }

    try {
      const response = await axios.get(
        `https://api.themoviedb.org/3/search/movie?api_key=67e39655ab5c0b2be23473d483fb4af4&query=${searchQuery}`
      );
      const movie = response.data.results[0];
      if (!movie) {
        alert("No movie found with that name.");
        return;
      }

      const videoResponse = await axios.get(
        `https://api.themoviedb.org/3/movie/${movie.id}/videos?api_key=67e39655ab5c0b2be23473d483fb4af4`
      );
      setImportedVideos(videoResponse.data.results);
    } catch (error) {
      console.error("Error fetching video data from TMDb:", error);
      alert("Error fetching video data.");
    }
  };

  const handleAddImportedVideo = (video) => {
    const formData = new FormData();
    formData.append('userId', user.userId); // Use userId from the logged-in user
    formData.append('movieId', tmdbId);
    formData.append('url', `https://www.youtube.com/watch?v=${video.key}`);
    formData.append('title', video.name);
    formData.append('description', video.description || "No description available");

    axios
      .post('/videos', formData, {
        headers: { 'Content-Type': 'multipart/form-data', Authorization: `Bearer ${accessToken}` },
      })
      .then((response) => {
        setVideoInfo([...videoInfo, response.data]);
        setImportedVideos([]);
      })
      .catch((error) => console.error("Error adding imported video:", error));
  };

  const renderForm = () => {
    const currentVideo = formState.mode === "update" ? formState.video : data;
    return (
      <div>
        <form>
          <label>
            Video URL
            <input type="text" name="url" value={currentVideo.url} onChange={handleOnChange} />
          </label>
          <label>
            Title
            <input type="text" name="title" value={currentVideo.title} onChange={handleOnChange} />
          </label>
          <label>
            Description
            <textarea name="description" value={currentVideo.description} onChange={handleOnChange} />
          </label>
        </form>
        <button onClick={formState.mode === "add" ? handleSave : handleUpdate}>
          {formState.mode === "add" ? "Save" : "Update"}
        </button>
      </div>
    );
  };

  return (
    <div>
      <button onClick={() => setFormState({ mode: formState.mode === "base" ? "add" : "base", video: {} })}>
        {formState.mode === "base" ? "Add Video" : "Cancel"}
      </button>
      <button onClick={handleImportVideos}>Import Videos from TMDb</button>
      <input
        type="text"
        placeholder="Search for a movie"
        value={searchQuery}
        onChange={(e) => setSearchQuery(e.target.value)}
      />
      {(formState.mode === "add" || formState.mode === "update") && renderForm()}

      {importedVideos.length > 0 && (
        <div>
          <h2>Imported Videos:</h2>
          <div>
            {importedVideos.map((video) => (
              <div key={video.id}>
                <h3>{video.name}</h3>
                <button onClick={() => handleAddImportedVideo(video)}>Add to My Videos</button>
              </div>
            ))}
          </div>
        </div>
      )}

      <div>
        {videoInfo.map((video) => (
          <div key={video.id}>
            <h1>{video.title}</h1>
            <p>{video.description}</p>
            <button onClick={() => setFormState({ mode: "update", video })}>Edit</button>
            <button onClick={() => handleDelete(video.id)}>Delete</button>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Videos;
